insert.selectKey:
SELECT COUNT(*) AS count FROM JOB_SEARCH WHERE STDNT_SN =
#{stdSn} AND
PBANC_SN = #{pbancSn}

choose.when:
UPDATE JOB_SEARCH
SET APLY_DT = #{nowDate},
JNCMP_APLY_YN = #{applyYn}, BRNO =
#{bizRegNum}
WHERE STDNT_SN =
#{stdSn} AND PBANC_SN = #{pbancSn}

mapper.select:
SELECT JNCMP_APLY_YN from JOB_SEARCH WHERE STDNT_SN =
#{stdSn} AND PBANC_SN
= #{pbancSn}

mapper.update:
UPDATE JOB_SEARCH
SET JNCMP_APLY_YN = 'N'
WHERE STDNT_SN = #{stdSn} AND PBANC_SN = #{pbancSn}
