mapper.insert:
INSERT INTO DSCSN_TTL (
DSCSN_TTL_SN,
DSCSN_TTL,
USE_YN
) VALUES (
#{itemID},
#{item},
#{use}
)

mapper.update:
UPDATE DSCSN_TTL
SET DSCSN_TTL = #{item},
USE_YN =
#{use}
WHERE DSCSN_TTL_SN = #{itemID}

mapper.select:
select count(cnsl_schedule_sn)
from dscsn_rsvt
where cnsl_schedule_sn = #{scheduleId}

SELECT COUNT(*) FROM
DSCSN_RSVT
WHERE stdnt_sn = #{studentId} AND cnsl_schedule_sn = #{scheduleId}
